mapper.select:
select u1.id ucId, u1.username ucName, u2.id upId, u2.username upName, p.id pid, p.name pName, ite.id disIteId, ite.name disIteName, bt.id btId, bt.name btName, req.id reqId, req.title reqName, bug.id, bug.title, bug.description, bug.status, bug.priority, bug.level, bug.reappear, bug.create_time, bug.modify_time, bug.creator_id, bug.processer_id, bug.bug_type_id btId, bug.project_id, bug.discovery_iterate_id disIteId, bug.requirement_id, bug.origin, bug.item_type, bug.deleted, bug.version from t_bug bug left join t_user u1 on bug.creator_id = u1.id left join t_user u2 on bug.processer_id = u2.id left join t_bug_type bt on bug.bug_type_id = bt.id left join t_project p on bug.project_id = p.id left join t_iteration ite on bug.discovery_iterate_id = ite.id left join t_requirement req on bug.requirement_id = req.id
and bug.title like concat(concat("%",#{title}),"%")
and bug.status=#{status}
and bug.priority=#{priority}
and bug.creator_id = #{creatorId}
and bug.processer_id = #{processerId}
and bug.project_id = #{projectId}
and bug.discovery_iterate_id = #{discoveryIteId}
and bug.deleted = 0
order by bug.create_time desc
